import { Result } from "./Result";
import React, { useState } from 'react';

function Board() {
    let buttonArray = ['C', '/', '*', 7, 8, 9, '-', 4, 5, 6, '+', 1, 2, 3, '0', '00', '.', '='];
    let [calc, setCalc] = useState(0);

    const updateScreen = (val) => {
        setCalc(calc.toString() + val.toString());
    }

    return (
        <div className="container">
            <form action="" name="calc" class="calculator">
                <Result calc={calc}/>
                {buttonArray.map((btn) => {
                    return <span className="num" onClick={() => updateScreen(btn)}><i> {btn} </i></span>
                })}
            </form>
        </div>
    );
}
export {Board};
